eth.query:
subscription ($query: String!, $sort: SORT, $low: Int64, $high: Int64, $limit: Int64) {
  searchTransactions(indexName: CALLS, query: $query, sort: $sort, lowBlockNum: $low, highBlockNum: $high, limit: $limit) {
    undo
    cursor
    block {
      hash
      number
    }
    node {
      hash
      from
      to
      value(encoding: ETHER)
      gasLimit
      gasPrice(encoding: ETHER)
      matchingCalls {
        from
        to
        value
        inputData
        returnData
        balanceChanges {
          address
          newValue(encoding: ETHER)
          reason
        }
        logs {
          address
          topics
          data
        }
        storageChanges {
          key
          oldValue
          newValue
        }
      }
    }
  }
}

